import { Component, OnInit } from "@angular/core";
import { AuthService } from "../auth.service";
import { FormGroup, FormBuilder, Validators } from "@angular/forms";
import { AuthData } from "../auth.Model";

@Component({
	selector: "app-signup",
	templateUrl: "./signup.component.html",
	styleUrls: ["./signup.component.css"],
})
export class SignupComponent implements OnInit {
	isLoading = false;
	signupForm: FormGroup;

	constructor(private fb: FormBuilder, private authService: AuthService) {}

	ngOnInit(): void {
		this.signupForm = this.fb.group({
			email: ["", [Validators.required, Validators.email]],
			username: ["", [Validators.required]],
			password: ["", [Validators.required], Validators.minLength(5)],
		});
	}

	OnSignup() {
		this.authService.CreateUser(
			this.signupForm.value.email,
			this.signupForm.value.username,
			this.signupForm.value.password
		);
		console.log(this.signupForm.value.email, this.signupForm.value.username, this.signupForm.value.password);
	}
}
<mat-spinner [diameter]="80" *ngIf="isLoading"></mat-spinner>
<div fxLayoutAlign="center" fxFlexFill class="main-div" *ngIf="!isLoading">
	<mat-card color="primary" fxFlex="50">
		<mat-card-title>
			<span>Sign Up</span>
		</mat-card-title>

		<form class="signUp" (submit)="OnSignup(loginForm)" #loginForm="ngForm">
			<mat-form-field>
				<input matInput type="email" #emailInput="ngModel" placeholder="Email" required />
			</mat-form-field>
			<mat-error *ngIf="emailInput.invalid">Please enter a valid email.</mat-error>

			<!-- <div
				class="alert alert-danger"
				*ngIf="signupForm.email.invalid && (signupForm.email.touched || signupForm.email.dirty)">
				<mat-error *ngIf="signupForm.email.errors.required">this is a required field</mat-error>
				<mat-error *ngIf="signupForm.errors.minlength">Min length is 7</mat-error>
			</div> -->

			<mat-form-field>
				<input matInput type="Text" #usernameInput="ngModel" placeholder="User Name" />
			</mat-form-field>
			<mat-error *ngIf="usernameInput.invalid">Please enter at least 5 letters.</mat-error>

			<!-- <div
				class="alert alert-danger"
				*ngIf="signupForm.username.invalid && (signupForm.username.touched || signupForm.username.dirty)">
				<mat-error *ngIf="signupForm.username.errors.required">this is a required field</mat-error>
				<mat-error *ngIf="signupForm.errors.minlength">Min length is 7</mat-error>
			</div> -->

			<mat-form-field>
				<input matInput type="password" #passwordInput="ngModel" placeholder="Password" />
			</mat-form-field>
			<mat-error *ngIf="passwordInput.invalid">Please enter at least 5 letters.</mat-error>

			<!-- <div
				class="alert alert-danger"
				*ngIf="signupForm.password.invalid && (signupForm.password.touched || signupForm.password.dirty)">
				<mat-error *ngIf="signupForm.password.errors.required">this is a required field</mat-error>
				<mat-error *ngIf="signupForm.password.errors.minlength">Min length is 10</mat-error>
			</div> -->
			<button class="save-button" mat-raised-button color="primary" type="submit"> LogIn</button>
		</form>
	</mat-card>
</div>
