import { HttpClient } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { Router } from "@angular/router";
import { Subject } from "rxjs";
import { AuthData } from "./auth.Model";

@Injectable({
	providedIn: "root",
})
export class AuthService {
	private url = "http://localhost:3000/api/user/";
	private AuthToken: string;
	private IsAuthenticated = false;
	private authStatusListner = new Subject<boolean>();
	private tokenTimer: any;

	constructor(private http: HttpClient, private router: Router) {}

	getAuthStatusListner() {
		return this.authStatusListner.asObservable();
	}

	getIsAuth() {
		return this.IsAuthenticated;
	}
	getAuthToken() {
		return this.AuthToken;
	}

	CreateUser(email: string, username: string, password: string) {
		const AuthData: AuthData = {
			email: email,
			username: username,
			password: password,
		};
		this.http.post(this.url + "signup", AuthData).subscribe(Response => {
			console.log(Response);
		});
	}

	logInUser(email: string, password: string) {
		const AuthData: AuthData = { email: email, password: password, username: null };
		this.http
			.post<{ message: string; token: string; expiresIn: number }>(
				"http://localhost:3000/api/user/login",
				AuthData
			)
			.subscribe(Response => {
				const token = Response.token;
				this.AuthToken = token;
				if (Response.token) {
					const expiresInDuration = Response.expiresIn;
					this.setAuthTimer(expiresInDuration);
					const now = new Date();
					const expirationTime = new Date(now.getTime() + expiresInDuration * 1000);
					console.log(expirationTime);
					this.saveAuthData(token, expirationTime);
					this.authStatusListner.next(true);
					this.IsAuthenticated = true;
					console.log(Response.message);
				}
			});
		this.router.navigate(["/"]);
	}
	logOutUser() {
		this.AuthToken = null;
		this.IsAuthenticated = false;
		this.authStatusListner.next(false);
		clearTimeout(this.tokenTimer);
		this.clearAuthData();
		this.router.navigate(["/"]);
	}

	private setAuthTimer(duration: number) {
		this.tokenTimer = setTimeout(() => {
			this.logOutUser();
		}, duration * 1000);
		console.log("Setting timer: " + duration);
	}

	private saveAuthData(token: string, duration: Date) {
		localStorage.setItem("token", token);
		localStorage.setItem("expiration", duration.toISOString());
	}

	private clearAuthData() {
		localStorage.removeItem("token");
		localStorage.removeItem("expiration");
	}
}
