import { Component, OnInit } from "@angular/core";
import { AuthService } from "../auth.service";
import { FormGroup, FormBuilder, Validators } from "@angular/forms";
import { AuthData } from "../auth.Model";

@Component({
	selector: "app-signup",
	templateUrl: "./signup.component.html",
	styleUrls: ["./signup.component.css"],
})
export class SignupComponent implements OnInit {
	isLoading = false;
	signupForm: FormGroup;

	constructor(private fb: FormBuilder, private authService: AuthService) {}

	ngOnInit(): void {
		this.signupForm = this.fb.group({
			email: ["", [Validators.required, Validators.email]],
			username: ["", [Validators.required]],
			password: ["", [Validators.required]],
		});
	}

	OnSignup() {
		this.authService.CreateUser(
			this.signupForm.value.email,
			this.signupForm.value.username,
			this.signupForm.value.password
		);
		console.log(this.signupForm.value.email, this.signupForm.value.username, this.signupForm.value.password);
	}
}
<mat-spinner [diameter]="80" *ngIf="isLoading"></mat-spinner>
<div fxLayoutAlign="center" fxFlexFill class="main-div" *ngIf="!isLoading">
	<mat-card color="primary" fxFlex="50">
		<mat-card-title>
			<span>Sign Up</span>
		</mat-card-title>

		<form class="signUp" [formGroup]="signupForm" (ngSubmit)="OnSignup()">
			<mat-form-field>
				<input matInput type="email" formControlName="email" placeholder="Email" required />
			</mat-form-field>
			<!-- <div class="alert alert-danger" *ngIf="title.invalid && (title.touched || title.dirty)">
				<mat-error *ngIf="title.errors.required">this is a required field</mat-error>
				<mat-error *ngIf="title.errors.minlength">Min length is 7</mat-error>
			</div> -->

			<mat-form-field>
				<input matInput type="Text" formControlName="username" placeholder="User Name" />
			</mat-form-field>

			<mat-form-field>
				<input matInput type="password" formControlName="password" placeholder="Password" />
			</mat-form-field>
			<!-- <div class="alert alert-danger" *ngIf="content.invalid && (content.touched || content.dirty)">
				<mat-error *ngIf="content.errors.required">this is a required field</mat-error>
				<mat-error *ngIf="content.errors.minlength">Min length is 10</mat-error>
			</div> -->
			<button class="save-button" mat-raised-button color="accent" type="submit"> LogIn</button>
		</form>
	</mat-card>
</div>
