import { Injectable } from "@angular/core";
import { Subject } from "rxjs";

@Injectable({ providedIn: "root" })
export class ErrorService {
	private errorListner = new Subject<string>();

	constructor() {}
	getErrorListner() {
		return this.errorListner.asObservable();
	}

	ThrowError(message: string) {
		return this.errorListner.next(message);
	}

	HandleError() {
		return this.errorListner.next(null);
	}
}
